/**
 * Type declarations for aframe-extras
 */
declare module 'aframe-extras' {
  // Since we're just using this as a global module that registers components with AFRAME,
  // we don't need to define specific exports
  const _default: any;
  export default _default;
}

// Also declare the individual modules if needed
declare module 'aframe-extras/src/controls' {
  const _default: any;
  export default _default;
}

declare module 'aframe-extras/src/loaders' {
  const _default: any;
  export default _default;
}

declare module 'aframe-extras/src/misc' {
  const _default: any;
  export default _default;
}

declare module 'aframe-extras/src/pathfinding' {
  const _default: any;
  export default _default;
}

declare module 'aframe-extras/src/primitives' {
  const _default: any;
  export default _default;
}
